import networkx as nx
import matplotlib.pyplot as plt
G = nx.MultiGraph()
v = ['a', 'b', 'c', 'd', 'e', 'f']
G.add_nodes_from(v)
G.add_node("a",morador="murilo",numero=100.0,seguranca="alta")
G.add_node("b",morador="Joao",numero=370.0,seguranca="baixa")
G.add_node("c",morador="Carlos",numero=225.0,seguranca="media")
G.add_node("d",morador="Nickson",numero=545.0,seguranca="alta")
G.add_node("e",morador="Stephanie",numero=453.0,seguranca="baixa")
G.add_node("f",morador="Diego",numero=701.0,seguranca="media")
G.add_edge('a','b',key='a1')
G.add_edge('a','b',key='a1',relacao="Vizinhos")
G.add_edge('a','c',key='a2')
G.add_edge('a','c',key='a2',relacao="Amigos")
G.add_edge('c','d',key='a3')
G.add_edge('c','d',key='a3',relacao="Primos")
G.add_edge('c','e',key='a4')
G.add_edge('c','e',key='a4',relacao="amigos")
G.add_edge('d','f',key='a5')
G.add_edge('d','f',key='a5',relacao="irmao")
nx.draw_networkx(G,with_labels=True)
plt.show()
G.remove_node('f')
plt.clf()
nx.draw_networkx(G,with_labels=True)
plt.show()
